import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface OpenSidebarState {
    open: boolean;
}

const initialState: OpenSidebarState = {
    open: true
}

const openSidebarSlice = createSlice ({
    name: 'OpenSidebar',
    initialState,
    reducers: {
        openSidebar: (state) => {
            state.open = true;
        },

        closeSidebar:  (state) => {
            state.open = false;
        }

    }    
});

export const { openModal, closeModal } = modalTaskSlice.actions;  

export default modalTaskSlice.reducer
