import * as React from "react";
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import Divider from "@mui/material/Divider";
import InboxIcon from "@mui/icons-material/Inbox";
import DraftsIcon from "@mui/icons-material/Drafts";

import TaskAltIcon from "@mui/icons-material/TaskAlt";
import WorkspacesIcon from "@mui/icons-material/Workspaces";
import StarsIcon from "@mui/icons-material/Stars";
import CabinIcon from "@mui/icons-material/Cabin";
import ShapeLineIcon from '@mui/icons-material/ShapeLine';
import AddTaskIcon from '@mui/icons-material/AddTask';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';
import StyleIcon from '@mui/icons-material/Style';

import Collapse from "@mui/material/Collapse";
import ExpandLess from "@mui/icons-material/ExpandLess";
import ExpandMore from "@mui/icons-material/ExpandMore";


function Sidebar() {
  const [selectedIndex, setSelectedIndex] = React.useState(1);
  const [open_w, setOpenW] = React.useState(true);
  const [open_a, setOpenA] = React.useState(true);

  const handleClick_w = () => {
    setOpenW(!open_w);
  };
  const handleClick_a = () => {
    setOpenA(!open_a);
  };

  const handleListItemClick = (
    event: React.MouseEvent<HTMLDivElement, MouseEvent>,
    index: number
  ) => {
    setSelectedIndex(index);
  };

  return (
    <Box
      sx={{
        width: "100%",
        height: "100vh",
        maxWidth: 360,
        bgcolor: "#f9f8f1",
      }}
    >
      <List sx={{pt:0, pb:0}} component="nav" aria-label="workspace">

        <ListItemButton onClick={handleClick_w}>
          <ListItemIcon>
            <CabinIcon />
          </ListItemIcon>
          <ListItemText primary="Workspace" />
          {open_w ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>
        <Collapse in={open_w} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <ListItemIcon sx={{ color: "BlueViolet" }}>
                <InboxIcon />
              </ListItemIcon>
              <ListItemText primary="Inbox" />
            </ListItemButton>

            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
              <ListItemIcon sx={{ color: "Coral" }}>
                <WorkspacesIcon />
              </ListItemIcon>
              <ListItemText primary="Active Tasks" />
            </ListItemButton>

            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 2}
              onClick={(event) => handleListItemClick(event, 2)}
            >
              <ListItemIcon sx={{ color: "CornflowerBlue" }}>
                <TaskAltIcon />
              </ListItemIcon>
              <ListItemText primary="Completed Task" />
            </ListItemButton>

            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 3}
              onClick={(event) => handleListItemClick(event, 3)}
            >
              <ListItemIcon sx={{ color: "DarkOliveGreen" }}>
                <StarsIcon />
              </ListItemIcon>
              <ListItemText primary="Favourites" />
            </ListItemButton>

          </List>
        </Collapse>
      </List>
      {/* <Divider /> */}
      <List sx={{pt:0, pb:0}}  component="nav" aria-label="action">

        <ListItemButton onClick={handleClick_a}>
          <ListItemIcon>
            <ShapeLineIcon />
          </ListItemIcon>
          <ListItemText primary="Action" />
          {open_a ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>
        <Collapse in={open_a} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <ListItemIcon sx={{ color: "DeepPink" }}>
                <AddTaskIcon />
              </ListItemIcon>
              <ListItemText primary="Add Task" />
            </ListItemButton>
            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <ListItemIcon sx={{ color: "SlateGray" }}>
                <RemoveCircleIcon />
              </ListItemIcon>
              <ListItemText primary="Delete Task" />
            </ListItemButton>



          </List>
        </Collapse>
      </List>

      <List sx={{pt:0, pb:0}}  component="nav" aria-label="styles">

        <ListItemButton onClick={handleClick_a}>
          <ListItemIcon>
            <StyleIcon />
          </ListItemIcon>
          <ListItemText primary="Action" />
          {open_a ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>
        <Collapse in={open_a} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <ListItemIcon sx={{ color: "DeepPink" }}>
                <AddTaskIcon />
              </ListItemIcon>
              <ListItemText primary="Add Task" />
            </ListItemButton>
            <ListItemButton
              sx={{ pl: 4 }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <ListItemIcon sx={{ color: "SlateGray" }}>
                <RemoveCircleIcon />
              </ListItemIcon>
              <ListItemText primary="Delete Task" />
            </ListItemButton>



          </List>
        </Collapse>
      </List>

      {/*  */}
      {/* <List component="nav" aria-label="secondary mailbox folder">
        <ListItemButton
          selected={selectedIndex === 2}
          onClick={(event) => handleListItemClick(event, 2)}
        >
          <ListItemText primary="Trash" />
        </ListItemButton>
        <ListItemButton
          selected={selectedIndex === 3}
          onClick={(event) => handleListItemClick(event, 3)}
        >
          <ListItemText primary="Spam" />
        </ListItemButton>
      </List> */}
    </Box>
  );
}

export default Sidebar;
